import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { 
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Menu, X, ChevronDown, User } from "lucide-react";
import anpereLogo from "@assets/Gemini_Generated_Image_vlpezzvlpezzvlpe_1758913669762.png";

import ChatGPT_Image_26_09_2025__20_19_29 from "@assets/ChatGPT Image 26_09_2025, 20_19_29.png";

import ChatGPT_Image_26_09_2025__20_24_00 from "@assets/ChatGPT Image 26_09_2025, 20_24_00.png";

import ChatGPT_Image_26_de_set__de_2025__20_37_30 from "@assets/ChatGPT Image 26 de set. de 2025, 20_37_30.png";

const FloatingMenu = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);
  const [location] = useLocation();

  useEffect(() => {
    const handleScroll = () => {
      const scrollTop = window.scrollY;
      const threshold = 100; // Pixels para ativar o efeito
      setIsScrolled(scrollTop > threshold);
    };

    window.addEventListener('scroll', handleScroll, { passive: true });

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  const publicationsItems = [
    { name: "Planos de Actividades", path: "/publicacoes" },
    { name: "Relatórios Anuais", path: "/publicacoes" },
    { name: "Actas de Reunião", path: "/publicacoes" },
    { name: "Comunicados", path: "/publicacoes" },
  ];

  const aboutItems = [
    { name: "Nossa História", path: "/quem-somos" },
    { name: "Missão e Visão", path: "/quem-somos" },
    { name: "Organograma", path: "/quem-somos" },
    { name: "Diretoria", path: "/quem-somos" },
